import styled from "styled-components";

export const CardContainer = styled.div`
  border: "1px solid";
  padding: 25px 12px 18px;
`;

export const Title = styled.h2`
  color: #fff;
  font-weight: 300;
  @media (max-width: 500px) {
    font-size: 1rem;
  }
`;
export const Description = styled.p`
  color: #fff;
  font-weight: 300;
  @media (max-width: 500px) {
    font-size: 0.75rem;
  }
`;
