import { useState } from "react";
import { AiFillEdit } from "react-icons/ai";
import {
  CardContainer,
  Title,
  Description,
  Row,
  Col,
  InfoContainer,
  Label,
  EditButton,
  DataContainter,
} from "./styles";
import EditModal from "./EditModal";

const Card = ({ item, isOpen, setOpen }) => {
  const { name, description, type, quantity } = item;
  return (
    <CardContainer>
      <Row>
        <DataContainter>
          <Col>
            <Title>{name}</Title>
            <Description>{description}</Description>
          </Col>
        </DataContainter>
        <InfoContainer>
          <Col>
            <Label>Tipo: {type}</Label>
            <Label>Quantidade: {quantity}</Label>
            <EditButton onClick={() => setOpen(true)}>
              <AiFillEdit /> Editar
            </EditButton>
          </Col>
        </InfoContainer>
      </Row>
      <EditModal
        isOpen={isOpen}
        onRequestClose={() => setOpen(false)}
        item={item}
      />
    </CardContainer>
  );
};

export default Card;
