import { AiFillEdit } from "react-icons/ai";
import {
  CardContainer,
  Title,
  Description,
  Row,
  Col,
  InfoContainer,
  Label,
  EditButton,
} from "./styles";

const Card = ({ name, description, type, quantity }) => {
  return (
    <CardContainer>
      <Row>
        <Col>
          <Title>{name}</Title>
          <Description>{description}</Description>
        </Col>
        <InfoContainer>
          <Col>
            <Label>Tipo: {type}</Label>
            <Label>Quantidade: {quantity}</Label>
            <EditButton>
              <AiFillEdit /> Editar
            </EditButton>
          </Col>
        </InfoContainer>
      </Row>
    </CardContainer>
  );
};

export default Card;
