import {
  CardContainer,
  Title,
  Description,
  Row,
  Col,
  InfoContainer,
  Label,
} from "./styles";

const Card = ({ name, description, type, quantity }) => {
  return (
    <CardContainer>
      <Row>
        <Col>
          <Title>{name}</Title>
          <Description>{description}</Description>
        </Col>
        <InfoContainer>
          <Col>
            <Row></Row>
            <Label>Tipo {type}</Label>
            <Description>{type}</Description>
          </Col>
        </InfoContainer>
      </Row>
    </CardContainer>
  );
};

export default Card;
