import { CardContainer, Title, Description, Row, Col } from "./styles";

const Card = ({ name, description, type, quantity }) => {
  return (
    <CardContainer>
      <Row>
        <Col>
          <Title>{name}</Title>
          <Description>{description}</Description>
        </Col>
      </Row>
    </CardContainer>
  );
};

export default Card;
